-- ----------------------------
-- Table structure for posts
-- ----------------------------
DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts` (
  `postId` int(11) NOT NULL AUTO_INCREMENT,
  `postAuthorId` int(11) DEFAULT NULL,
  `postDate` datetime DEFAULT NULL,
  `postTitle` text,
  `postName` text,
  `postContent` longtext,
  `postExcerpt` text,
  `postStatus` enum('active','deleted','inactive') DEFAULT 'active',
  `postParent` int(11) DEFAULT NULL,
  `postType` enum('post','page','picture','album') DEFAULT 'post',
  `postCommentCount` int(11) DEFAULT NULL,
  `postGuid` varchar(255) DEFAULT NULL,
  `postTermId` int(11) DEFAULT NULL,
  `postThemeId` int(11) DEFAULT NULL,
  PRIMARY KEY (`postId`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

