DROP TABLE IF EXISTS `settings`;
CREATE TABLE `settings` (
  `settingId` int(11) NOT NULL AUTO_INCREMENT,
  `settingKey` text,
  `settingCaption` text,
  `settingValue` text,
  `settingType` enum('text') DEFAULT 'text',
  PRIMARY KEY (`settingId`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

